import React from 'react'
import { LinkContainer } from 'react-router-bootstrap';
import { Container,Navbar,Nav,NavDropdown } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import {useDispatch} from 'react-redux';
import { resetCart } from '../app/features/slices/cartslice';
import logo from '../assets/naturely.png'

const Header = () => {
  const navigate = useNavigate();
  const dispatch=useDispatch();
  let userInfo;
  if(localStorage.getItem('userInfo') !== null || localStorage.getItem('userInfo') !== undefined){
    userInfo=JSON.parse(localStorage.getItem('userInfo'))
    // console.log(userInfo.isAdmin)
  }


  // const myordersrefetch=async()=>{
  //       await getMyOrders()
  // }

  // const resetApiState = () => ({
  //   type: string,
  //   payload: undefined,
  // })
    // const {refetch} = useGetMyOrdersQuery(skipToken);

  const logoutHandler=()=>{
    dispatch(resetCart())
    // dispatch(useGetMyOrdersQuery.util.invalidateTags());
    // dispatch(useGetMyOrdersQuery.util.invalidateTags(['Refetchorders']))
    // refetch()
    localStorage.removeItem('userInfo')
    // getMyOrders()
    // dispatch(api.util.resetApiState())
    navigate('/')
  }



  return (
    <header>
      <Navbar bg="#fcfaf8" variant='dark' expand="lg" collapseOnSelect style={{backgroundColor:'#fcfaf8'}}>
      <Container>
        <LinkContainer to='/'>
        <Navbar.Brand>
              <img src={logo} width='180' height={80} className='d-inline-block align-top'/>
        </Navbar.Brand>
        </LinkContainer>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
              <LinkContainer to='/cart'>
                <Nav.Link style={{color:"#000"}}>
                  <i className='fas fa-shopping-cart'></i>Cart
                </Nav.Link>
              </LinkContainer>
            {userInfo !== null ? (
                <NavDropdown title={userInfo.name} id='username'>
                  <LinkContainer to='/profile'>
                    <NavDropdown.Item>Profile</NavDropdown.Item>
                  </LinkContainer>
                  <NavDropdown.Item onClick={logoutHandler}>
                    Logout
                  </NavDropdown.Item>
                </NavDropdown>
              ) : (
                <LinkContainer to='/login'>
                  <Nav.Link>
                    <i className='fas fa-user'></i> Sign In
                  </Nav.Link>
                </LinkContainer>
              )}
              {userInfo && userInfo.isAdmin && (
                <NavDropdown title="Admin" id='adminmenu'>
                <LinkContainer to='/admin/userlist'>
                  <NavDropdown.Item>Users</NavDropdown.Item>
                </LinkContainer>
                <LinkContainer to='/admin/productlist'>
                  <NavDropdown.Item>Products</NavDropdown.Item>
                </LinkContainer>
                <LinkContainer to='/admin/orderlist'>
                  <NavDropdown.Item>Orders</NavDropdown.Item>
                </LinkContainer>
              </NavDropdown>
              )}
            {/* <LinkContainer to='/login'>
            <Nav.Link><i className='fas fa-user'></i>Sign in</Nav.Link>
            </LinkContainer> */}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
    </header>
  )
}


export default Header
